<template>
  <div class="exception">
    <img :src="currentInfo.img" class="exception-img" alt="页面不存在">
    <div class="exception-prompt">
      <h3 class="exception-title">{{currentInfo.title}}</h3>
      <p class="exception-description">{{currentInfo.description}}，请<a href='###'>返回</a></p></div>
  </div>
</template>


<script>
  const infoMap = {
    '/404': {
      img: 'data:image/png;base64,',
      title: '抱歉，你访问的页面不存在',
      description: '您要找的页面没有找到'
    }
  }
  export default {
    name: 'Exception',
    data() {
      return {
        info: {
          img: '',
          title: '',
          description: ''
        }
      }
    },
    computed: {
      currentInfo() {
        const { path } = this.$route;
        return infoMap[path];
      }
    }
  }
</script>

<style lang="scss" scoped>
  .exception {
    height: 100%;
    display: flex;
    justify-content: center;
    align-items: center;
    #{&}-img {
      margin-right: 50px;
    }
    #{&}-title {
      color: rgb(51, 51, 51);
      font-size: 24px;
      margin: 24px 0;
    }
    #{&}-description {
      color: rgb(102, 102, 102);
      font-size: 16px;
    }
  }
</style>



